<script setup>
import { ref } from 'vue'

const posX = ref(0)
const posY = ref(0)

document.addEventListener('mousemove', showMousePosition, false)

function showMousePosition(event) {
  posX.value = event.clientX
  posY.value = event.clientY
  document.querySelector('#cursor .inner').style.opacity = 1
}

document.body.onmouseleave = function () {
  document.querySelector('#cursor .inner').style.opacity = 0
}

document.body.onmouseenter = function () {
  document.querySelector('#cursor .inner').style.opacity = 1
}

document.body.onclick = function () {
  let a = document.createElement('div')
  a.classList.add('click')
  a.style.left = posX.value - 75 / 2 + 'px'
  a.style.top = posY.value - 75 / 2 + 'px'
  document.body.appendChild(a)
  setTimeout(() => {
    a.remove()
  }, 500)
}

document.oncontextmenu = function () {
  return false
}

let el = window.document.body
window.document.body.onmouseover = function (event) {
  el = event.target
  if (
    el.tagName === 'A' ||
    el.tagName === 'BUTTON' ||
    el.classList.contains('css-cursor-hover-enabled') ||
    el.parentElement.classList.contains('css-cursor-hover-enabled') ||
    el.parentElement.parentElement.classList.contains('css-cursor-hover-enabled')
  ) {
    document.querySelector('#cursor').classList.add('hover')
  } else {
    document.querySelector('#cursor').classList.remove('hover')
  }
}
</script>

<template>
  <div
    id="cursor"
    class="cursor"
    :style="{
      transform: `translate(${posX}px, ${posY}px)`
    }"
  >
    <div class="inner"></div>
  </div>
</template>

<style>
* {
  cursor: none !important;
}

.click {
  width: 75px;
  height: 75px;
  border-radius: 100%;
  background: #c3ebff;
  position: fixed;
  left: 0;
  top: 0;
  transform: scale(0%);
  border: 3px solid transparent;
  filter: drop-shadow(0px 0px 6px #c3ebff);
  animation: move 0.5s ease-out;
}

@keyframes move {
  0% {
    transform: scale(0%);
    border: 3px solid #77deff;
    background: #c3ebff;
  }
  25% {
    transform: scale(100%);
    border: 3px solid #fff;
    background: #c3ebff66;
  }
  50% {
    transform: scale(100%);
    border: 3px solid #fff;
    background: transparent;
  }
  80% {
    transform: scale(100%);
    border: 3px solid transparent;
    background: transparent;
  }
  100% {
    transform: scale(100%);
    border: 3px solid transparent;
    background: transparent;
  }
}
</style>

<style scoped>
@media (hover: none) {
  #cursor {
    display: none;
  }
}
#cursor {
  pointer-events: none;
  z-index: 9999;
  position: fixed;
  top: 0;
  left: 0;
}

#cursor::after {
  content: '';
  opacity: 0;
  transition: all 0.3s ease;
  background: url(data:image/png;base64,)
    no-repeat;
  background-size: 100%;
  width: 1.3666666667rem;
  height: 1.4666666667rem;
  display: inline-block;
  position: absolute;
  top: 0;
  left: 0;
  z-index: -1;
  transform: translate(-50%, -30%);
}

#cursor.hover::after {
  opacity: 1;
}

#cursor .inner {
  background-image: url(data:image/png;base64,);
  box-shadow: initial !important;
  border-radius: initial !important;
  background-size: 100% !important;
  width: 1.4333333333rem !important;
  height: 1.7333333333rem !important;
  display: inline-block !important;
  transition: all 0.3s ease;
  transform: translate(-0.3333333333rem) !important;
  opacity: 0;
}
</style>
